import React, {useState} from 'react'
import {Link, useHistory} from 'react-router-dom'
import M from 'materialize-css'

const Signup = ()=>{

    const history = useHistory()
    const [name, setName] = useState("")
    const [password, setPassword] = useState("")
    const [email, setEmail] = useState("")
    
    const PostData = ()=>{
        if(!/^(([^<>()\[\]\\.,;:\s@"]+(\.[^<>()\[\]\\.,;:\s@"]+)*)|(".+"))@((\[[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}])|(([a-zA-Z\-0-9]+\.)+[a-zA-Z]{2,}))$/.test(email)){
            M.toast({html: "Invalid Email id", classes:"#b71c1c red darken-4"})
            return
        }
        fetch("/signup",{
            method:"post",
            headers:{
                "Content-Type":"application/json"
            },
            body:JSON.stringify({
                name,
                password,
                email
            })
        }).then(res=>res.json())
        .then(data=>{
            if(data.error){
                M.toast({html: data.error, classes:"#b71c1c red darken-4"})
            }
            else{
                M.toast({html: data.message, classes:"#76ff03 light-green accent-4"})
                history.push('/signin')
            }
        })
    }
    return(
        <div className="mycard">
            <div className="card auth-card input-field">
            <h3>Sign-up</h3>
            <input
            type = "text"
            placeholder="Name"
            value = {name}
            onChange = {(e)=>setName(e.target.value)}
            />
            <input
            type = "text"
            placeholder="email"
            value = {email}
            onChange = {(e)=>setEmail(e.target.value)}
            />
            <input
            type = "text"
            placeholder="password"
            value = {password}
            onChange={(e)=>setPassword(e.target.value)}
            />
            <button className="btn waves-effect waves-light #e57373 red lighten-2"
            onClick={()=>PostData()}>
            Sign up
            </button>
            <h6>
                <Link to="./signin">Account already exists?</Link>
            </h6>
            </div>
        </div>
    )
}

export default Signup
